mapper.update:
update userInfo
set userName = #{username,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  passWord = #{password,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, userName, sex, age, passWord
from userInfo
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into userInfo (id, userName, age, 
  passWord, sex, CHName
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{age,jdbcType=INTEGER}, 
  #{password,jdbcType=VARCHAR}, #{sex,jdbcType=VARBINARY}, #{chname,jdbcType=VARBINARY}
  )

